import { Cookies } from 'react-cookie';
import useSWR from 'swr';
import axios from 'axios';

export function handleAuth() {
  // set up cookies
  const cookies = new Cookies();
  const fetcher = url => axios.get(url, { headers: { 'Authorization': token } });
  const { data, error } = useSWR(`${process.env.SERVER}/api/ping`, fetcher);
  if (error) {
    return false;
  }
  if (!data) {
    return null;
  }

  console.log("token ping:", data.msg);
  return true;
}
