import { Formik, Form, Field, ErrorMessage } from 'formik'
import Btn from '../components/btn';

export default function SignIn({ label, type, name }) {
  return (
    <>
      <Formik
        initialValues={{ email: '', password: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'email required';
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = 'Invalid email address';
          }
          if (!values.password) {
            errors.password = 'password required'
          } else if (!/^(?=.*\d)(?=.*[^\w\d\s])(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/.test(values.password)) {
            if (!/^(?=.*\d)$/.test(values.password)) { errors.password = 'numbers' }
            if (!/^(?=.*[a-z])$/.test(values.password)) { errors.password = 'lowercase' }
            if (!/^(?=.*[A-Z])$/.test(values.password)) { errors.password = 'uppercase' }
            if (!/^(?=.*[^\w\d\s])$/.test(values.password)) { errors.password = 'special characters' }
            if (!/^.{8,}$/.test(values.password)) { errors.password = 'at least 8 characters' }
            // errors.password = 'uppercase, lowercase, numbers, special characters, at least 8';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field
              placeholder="email"
              className="primary"
              type="email"
              name="email"
            />
            <ErrorMessage name="email" />
            <Field
              placeholder="password"
              className="primary"
              type="password"
              name="password"
            />
            <ErrorMessage name="password" component="div" />
            <Btn primary submit disabled={isSubmitting}>
              sign in
            </Btn>
          </Form>
        )}
      </Formik>
    </>
  )
}
