import React, { useState } from 'react'
import Head from 'next/head';
import Link from 'next/link';
import Layout, { siteTitle } from '../components/layout';
import utilStyles from '../styles/utils.module.css';
import { Cookies } from "react-cookie";
import useSWR from 'swr';

const serverUrl = `http://localhost:3001`;

// set up cookies
const cookies = new Cookies();

export default function Home() {
  const [token, setToken] = useState(cookies.get('token') || null);

  const onLoginClick = async () => {
    console.log("login called");
    const response = await fetch(serverUrl + '/api/login', { method: "GET" }).then((res) => {
      return res.json();
    }).catch((err) => {
      if (err) throw err;
    });
    const theToken = JSON.stringify(response);
    cookies.set('token', theToken);
    setToken({ token: theToken });
  }

  async function mongo() {
    const { data, error } = await useSWR('/api/login', fetch);

    if (error) return <div>failed to load</div>
    if (!data) return <div>loading...</div>

    // render data
    return <div>hello {data}</div>
  }

  return (
    <Layout home>
      <Head>
        <title>{siteTitle}</title>
      </Head>
      <section className={utilStyles.headingMd}>
        <p>I'm Doug McKay</p>
        <p>
          (This is a sample website - youâ€™ll be building a site like this on{' '}
          <a href="https://nextjs.org/learn">our Next.js tutorial</a>.)
        </p>
        <button onClick={() => onLoginClick()}>Get Token</button>
        <p>Token: {token}</p>
        <Link href="/secret"><a>Secret Page</a></Link>
      </section>
    </Layout>
  )
}
