import React, { useEffect, useState } from 'react'
import Head from 'next/head';
import { useRouter } from 'next/router';
import Layout, { siteTitle } from '../components/layout';
import utilStyles from '../styles/utils.module.css';
import { Cookies } from "react-cookie";
import useSWR from 'swr';
import axios from 'axios';
import Btn from '../components/btn'
import SignIn from '../components/signIn';





export default function Home({ serverUrl }) {
  const [needSignUp, setNeedSignUp] = useState(true);
  // set up cookies
  const fetcher = url => axios.get(url).then(res => res.data);
  const cookies = new Cookies();
  const { data, error } = useSWR(serverUrl + '/api/login', fetcher);
  if (error) return 'failed to load token' + JSON.stringify(error);
  if (!data) return 'loading...';


  // console.log('data token ', data.token);
  const theToken = data.token;
  cookies.set('token', theToken);

  // let signWhich = needSignUp;

  return (
    <Layout home>
      <Head>
        <title>{siteTitle}</title>
      </Head>
      <section className={utilStyles.headingMd}>
        {needSignUp && <><SignIn signin /><Btn onClick={() => setNeedSignUp(false)} >sign up</Btn></> || <SignIn signup />}
        <Btn link="/dashboard" primary >dashboard</Btn>
      </section>
    </Layout>
  )
}

export async function getStaticProps(ctx) {
  return {
    props: {
      serverUrl: process.env.SERVER
    }
  }
}
