import { Cookies } from 'react-cookie';
import { handleAuth } from '../components/utils/auth';
import useSWR from 'swr';
import axios from 'axios';
import Router from 'next/router';

// set up cookies

export default function Dashboard(ctx) {
  const cookies = new Cookies();
  const token = cookies.get('token');
  const fetcher = url => axios.get(url).then(res => res.data);
  const { data, error } = useSWR(`${process.env.SERVER}/api/ping`, fetcher);

  if (error) {
    Router.push('/');
    return <div>error loading. probably not authorized.</div>
  }
  if (!data) {
    return <div>loading...</div>
  }

  return (
    handleAuth(token) && <div><h2>secret page</h2></div>
  )
}

export async function getInitialProps(ctx) {
  // must validate jwt
  // if jwt is invalid we redirect to home
  return {
    props: {
      handleAuth: handleAuth()
    }
  }
}
