import React, { useState } from 'react'
import Head from 'next/head';
import Link from 'next/link';
import Layout, { siteTitle } from '../components/layout';
import utilStyles from '../styles/utils.module.css';
import { Cookies } from "react-cookie";
import useSWR from 'swr';
import axios from 'axios';
import Btn from '../components/btn'
import SignIn from './signIn';

const fetcher = url => axios.get(url).then(res => res.data);

export function Mongo({ serverUrl }) {
  const { data, error } = useSWR(serverUrl + '/api/login', fetch);

  if (error) return <div>failed to load</div>
  if (!data) return <div>loading...</div>

  // render data
  return <div>hello {data}</div>
}

export default function Home({ serverUrl }) {
  // set up cookies
  const cookies = new Cookies();
  const [token, setToken] = useState(cookies.get('token') || null);
  const { data, error } = useSWR(serverUrl + '/api/login', fetcher);
  if (error) return 'failed to load token';
  if (!data) return 'loading...';

  // console.log('data token ', data.token);
  const theToken = data.token;
  cookies.set('token', theToken);


  return (
    <Layout home>
      <Head>
        <title>{siteTitle}</title>
      </Head>
      <section className={utilStyles.headingMd}>
        <p>I'm Doug McKay</p>
        <p>
          (This is a sample website - youâ€™ll be building a site like this on{' '}
          <a href="https://nextjs.org/learn">our Next.js tutorial</a>.)
        </p>
        <button onClick={() => setToken(theToken)}>Get Token</button>
        <p>Token: {JSON.stringify(token)}</p>
        <SignIn />
        <Btn link="/dashboard" primary className="signin">sign in</Btn>
        <Btn onClick={() => alert('hello')} className="signup">sign up</Btn>
      </section>
    </Layout>
  )
}

export async function getStaticProps(ctx) {
  return {
    props: {
      serverUrl: process.env.SERVER
    }
  }
}
