import { Cookies } from 'react-cookie';
import { handleAuth } from '../components/utils/auth';
import useSWR from 'swr';
import axios from 'axios';
import Router from 'next/router';

// set up cookies

export default function Dashboard({ server, handleAuth }) {
  const cookies = new Cookies();
  const token = cookies.get('token');
  const fetcherAuth = url => axios.get(url, { headers: { 'Authorization': token } });
  const { data, error } = useSWR(`${server}/api/ping`, fetcherAuth);

  if (error) {
    // Router.push('/');
    return <div>error loading. probably not authorized. {JSON.stringify(error)}</div>
  }
  if (!data) {
    return <div>loading...</div>
  }

  return (
    handleAuth(token) && <div><h2>secret page</h2></div>
  )
}

export async function getInitialProps(ctx) {
  // must validate jwt
  // if jwt is invalid we redirect to home
  return {
    props: {
      handleAuth: handleAuth(ctx),
      server: process.env.SERVER
    }
  }
}
