import { Formik, Form, Field, ErrorMessage } from 'formik'
import Btn from './btn';
import axios from 'axios';
import { Cookies } from "react-cookie";
import useSWR from 'swr'
import { useState } from 'react';

export function ErrorMsg({ children }) {
  return <div className="errorMsg">{children}</div>
}

export function Mongo({ serverUrl }) {
  const fetcher = (url) => fetch(``, {
    method: "POST",
    headers: {
      'Content-type': 'application/json',
    },
    body: JSON.stringify(values)
  }).then(res => res.json());
  const { data, error } = useSWR(serverUrl + '/api/createuser', fetcher);

  if (error) {
    console.error(error);
    return <div>problem</div>
  }
  if (!data) return <div>loading...</div>

  // render data
  return <div>added user. {data}</div>
}

export default function SignIn({ signin, signup, serverUrl }) {
  const createUser = (values) => {
    try {
      fetch(serverUrl + '/api/createuser',
        {
          method: "POST",
          headers: {
            'Content-type': 'application/json',
          },
          body: JSON.stringify(values)
        })
    } catch (err) {
      if (err) console.error(err);
    }
  }
  return (
    <>
      <Formik
        initialValues={{ email: '', password: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'email required';
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = 'Invalid email address';
          }
          if (!values.password) {
            errors.password = 'password required'
          } else if (signup && !/^(?=.*\d)(?=.*[!\"#$%&'()*+,\-.\/:;<=>?@\[\\\]^_`{|}~])(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z])([a-z]|[A-Z]|[0-9]|[!\"#$%&'()*+,\-.\/:;<=>?@\[\\\]^_`{|}~]){8,}$/.test(values.password)) {
            errors.password = ['needs to add:'];
            if (!/(?=.*\d)/.test(values.password)) {
              errors.password.push('numbers')
            } else {
              errors.password = errors.password.filter(m => m !== 'numbers');
            }
            if (!/(?=.*[!\"#$%&'()*+,\-.\/:;<=>?@\[\\\]^_`{|}~])/.test(values.password)) {
              if (errors.password.findIndex(m => m === 'special characters') === -1) {
                errors.password.push('special characters');
              }
            } else {
              errors.password = errors.password.filter(m => m !== 'special characters');
            }
            if (!/(?=.*[a-z])/.test(values.password)) {
              errors.password.push('lowercase')
            } else {
              errors.password = errors.password.filter(m => m !== 'lowercase');
            }
            if (!/(?=.*[A-Z])/.test(values.password)) {
              errors.password.push('uppercase')
            } else {
              errors.password = errors.password.filter(m => m !== 'uppercase');
            }
            if (!/.{8,}/.test(values.password)) {
              errors.password.push('at least 8')
            } else {
              errors.password = errors.password.filter(m => m !== 'at least 8');
            }
            errors.password = errors.password.join(',')
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          signin &&
            setTimeout(() => {
              setToken()
              goToUser(serverUrl, values);
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          signup &&
            setTimeout(() => {
              createUser(values);
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field
              placeholder="email"
              className="primary"
              type="email"
              name="email"
            />
            <ErrorMessage name="email" render={msg => <ErrorMsg>{msg}</ErrorMsg>} />
            <Field
              placeholder="password"
              className="primary"
              type="password"
              name="password"
            />
            <ErrorMessage name="password" render={msg => msg.split(',').map(m => <ErrorMsg key={m}>{m}</ErrorMsg>)} />
            <Btn name="submit" primary submit disabled={isSubmitting}>
              {signin && 'sign in' || signup && 'sign up'}
            </Btn>
            <ErrorMessage name="submit" />
          </Form>
        )}
      </Formik>
    </>
  )
}


export async function goToUser(serverUrl, values) {
  // set up cookies
  const fetcher = url => axios.get(url).then(res => res.data);
  const cookies = new Cookies();
  const { data, error } = useSWR(serverUrl + '/api/login', fetcher);
  if (error) console.log('failed to load token' + JSON.stringify(error));
  if (!data) console.log('loading...');


  // console.log('data token ', data.token);
  const theToken = data.token;
  cookies.set('token', theToken);
}

export async function getStaticProps(ctx) {
  return {
    props: {
      serverUrl: process.env.SERVER,
    }
  }
}
