import { Cookies } from 'react-cookie';
import handleAuth from '../components/utils/auth';
import useSWR from 'swr';
import axios from 'axios';
import Router from 'next/router';

// set up cookies

export default function Dashboard({ serverUrl }) {
  const cookies = new Cookies();
  const token = cookies.get('token');
  const fetcherAuth = url => axios.get(url, { headers: { 'Authorization': token } }).then(res => res.data);
  const { data, error } = useSWR(`${serverUrl}/api/ping`, fetcherAuth);

  if (error) {
    // Router.push('/');
    return <div>error loading. probably not authorized. {JSON.stringify(error)}</div>
  }
  if (!data) {
    return <div>loading...</div>
  }

  console.log(handleAuth(token));

  return (
    handleAuth && <div><h2>secret page</h2></div> || <div>auth isn't working</div>
  )
}

export async function getInitialProps(ctx) {
  // must validate jwt
  // if jwt is invalid we redirect to home
  return {
    props: {
      isAuthd: handleAuth()
    }
  }
}

export async function getStaticProps(ctx) {
  return {
    props: {
      serverUrl: process.env.SERVER
    }
  }
}
