import { Cookies } from 'react-cookie';
import useSWR from 'swr';
import axios from 'axios';
import Router from 'next/router';
import Btn from '../components/btn'

// set up cookies

export default function Dashboard(ctx) {
  const logOut = () => {
    // cookies.remove('token', null);
    // cookies.remove('refreshToken', null);
    cookies.remove('status', null);
    Router.push('/');
  }
  const cookies = new Cookies();
  const token = cookies.get('token');
  const fetcherAuth = url => axios.post(url, { headers: { 'Authorization': token }, body: { refreshToken: cookies.get('refreshToken') } }).then(res => res.data);
  const { data, error } = useSWR(`${ctx.serverUrl}/api/token`, fetcherAuth);
  if (error) {
    // console.error(error.response.data.msg)
    logOut();
    return <div>error loading. probably need to login again.</div>
  }
  if (!data) {
    return <div>loading...</div>
  }

  return (
    <div><h2>secret page</h2> <Btn primary onClick={() => logOut()}>log out</Btn>{JSON.stringify(data)}</div>
  )
}

export async function getStaticProps(ctx) {
  return {
    props: {
      serverUrl: process.env.SERVER
    }
  }
}
