import { Formik, Form, Field, ErrorMessage } from 'formik'
import Btn from '../components/btn';

export function ErrorMsg({ children }) {
  return <div className="errorMsg">{children}</div>
}

export default function SignIn({ label, type, name }) {
  return (
    <>
      <Formik
        initialValues={{ email: '', password: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'email required';
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = 'Invalid email address';
          }
          if (!values.password) {
            errors.password = 'password required'
          } else if (!/^(?=.*\d)(?=.*[!\"#$%&'()*+,\-.\/:;<=>?@\[\\\]^_`{|}~])(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z])([!\"#$%&'()*+,\-.\/:;<=>?@\[\\\]^_`{|}~]){8,}$/.test(values.password)) {
            errors.password = [];
            if (!/(?=.*\d)/) {
              errors.password.push('numbers')
            } else {
              errors.password = errors.password.filter(m => m !== 'numbers');
            }
            errors.password.join(',');
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field
              placeholder="email"
              className="primary"
              type="email"
              name="email"
            />
            <ErrorMessage name="email" />
            <Field
              placeholder="password"
              className="primary"
              type="password"
              name="password"
            />
            <ErrorMessage name="password" render={msg => msg.split(',').map(m => <ErrorMsg>{m}</ErrorMsg>)} />
            <Btn primary submit disabled={isSubmitting}>
              sign in
            </Btn>
          </Form>
        )}
      </Formik>
    </>
  )
}
