import { Cookies } from 'react-cookie';
import useSWR from 'swr';
import axios from 'axios';

export function handleAuth(ctx) {
  // set up cookies
  const cookies = new Cookies();
  let token = null;

  // if context has request info aka Server Side
  if (ctx.req) {
    // ugly way to get cookie value from a string of values
    // good enough for demostration
    token = ctx.req.headers.cookie.replace(/(?:(?:^|.*;\s*)token\s*\=\s*([^;]*).*$)|^.*$/, "$1");
  }
  else {
    // we dont have request info aka Client Side
    token = cookies.get('token')
  }

  const fetcher = url => axios.get(url, { headers: { 'Authorization': token } });
  const { data, error } = useSWR(`${process.env.SERVER}/api/ping`, fetcher);
  if (error) {
    console.log(error.response.data.msg);
    console.log("redirecting back to main page");
    if (ctx.res) {
      ctx.res.writeHead(302, {
        Location: '/'
      })
      ctx.res.end()
    } else {
      Router.push('/')
    }
    return false;
  }
  if (!data) {
    return null;
  }

  console.log("token ping:", data.msg);
  return true;
}
