import { Cookies } from 'react-cookie';
import useSWR from 'swr';
import axios from 'axios';
import Router from 'next/router';
import Btn from '../components/btn'

// set up cookies

export default function Dashboard(ctx) {
  const cookies = new Cookies();
  const token = cookies.get('token');
  const fetcherAuth = url => axios.get(url, { "token": token }).then(res => res.data);
  const { data, error } = useSWR(`${ctx.serverUrl}/api/ping`, fetcherAuth);
  const logOut = () => {
    cookies.set('token', null);
    cookies.set('refreshToken', null);
    cookies.set('status', null);
    Router.push('/');
  }

  if (error) {
    console.log(error.response.data.msg);
    console.log("redirecting back to main page");
    if (ctx.res) {
      ctx.res.writeHead(302, {
        Location: '/'
      })
      ctx.res.end()
    } else {
      Router.push('/')
    }
    return <div>error loading. probably not authorized.</div>
  }
  if (!data) {
    return <div>loading...</div>
  }

  return (
    <div><h2>secret page</h2> <Btn primary onClick={() => logOut()}>log out</Btn>{data}</div>
  )
}

export async function getStaticProps(ctx) {
  return {
    props: {
      serverUrl: process.env.SERVER
    }
  }
}
