import { Formik, Form, Field, ErrorMessage } from 'formik'
import Btn from '../components/btn';

export default function SignIn({ label, type, name }) {
  return (
    <>
      <Formik
        initialValues={{ email: '', password: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'email required';
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = 'Invalid email address';
          }
          if (!values.password) {
            errors.password = 'password required'
          } else if (!/^((?=.*[\d])(?=.*[a-z])(?=.*[A-Z])(?=.*[a-z])(?=.*[^\w\d\s]))((.*[\d])(.*[a-z])(.*[A-Z])(.*[a-z])(.*[^\w\d\s]))|(([\d])|([a-z])|([A-Z])|([a-z])|([^\w\d\s])){8,30}$/.test(values.password)) {
            errors.password = 'must include upper and lowercase, numbers, special characters, and be between 8 and 30 characters';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field
              placeholder="email"
              className="primary"
              type="email"
              name="email"
            />
            <ErrorMessage name="email" />
            <Field
              placeholder="password"
              className="primary"
              type="password"
              name="password"
            />
            <ErrorMessage name="password" />
            <Btn primary submit disabled={isSubmitting}>
              sign in
            </Btn>
          </Form>
        )}
      </Formik>
    </>
  )
}
